import math

def solve():
    t = int(input())
    for _ in range(t):
        n = int(input())
        a = list(map(int, input().split()))
        for m in range(int(math.log2(n)), 1, -1):
            for i in range(2**m):
                if 1 <= i <= n:
                    a[i-1] -= 1
        if all(a[i-1] <= a[i] for i in range(1, n)):
            print("YES")
            break
        else:
            print("NO")
