from collections import deque

def solve():
    input = sys.stdin.readline
    t = int(input.strip())
    for _ in range(t):
        input = sys.stdin.readline
        n = int(input.strip())
        input = sys.stdin.readline
        a = list(map(int, input.strip().split()))
        even = deque()
        odd = deque()
        for i in a:
            if i % 2 == 0:
                even.append(i)
            else:
                odd.append(i)
        even = sorted(even, reverse=True)
        odd = sorted(odd, reverse=True)
        mihai = 0
        bianca = 0
        turn = True
        while even or odd:
            if turn:
                if even and (not odd or even[0] > odd[0]):
                    mihai += even.popleft()
                elif odd:
                    bianca += odd.popleft()
                else:
                    break
            else:
                if odd and (not even or odd[0] > even[0]):
                    bianca += odd.popleft()
                elif even:
                    mihai += even.popleft()
                else:
                    break
            turn = not turn
        print("YES" if mihai > bianca else "NO")

