        r"^[X]*M$": lambda x: (len(x) - 1, 1),
        r"^[X]*L$": lambda x: (len(x) - 1, 2)
    }

    def size(s):
        for pattern, func in patterns.items():
            match = re.match(pattern, s)
            if match:
                return func(match.group())

