from collections import OrderedDict

def solve():
    t = int(input())
    for _ in range(t):
        n = int(input())
        s = input()
        d = OrderedDict()
        ans = 0
        for i in s:
            if i not in d:
                d[i] = 1
                ans += 2
            else:
                ans += 1
        print(ans)

